package JavaProject;

public class Ternaty_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =400;
		int b=200;
		String output = (a%2==0) ? " Even " : " Odd  ";
		System.out.println(output);
		String str=(a>b)?"a":"b";
		System.out.println(str);
		
	}

}
